export function rounddiv(x, y) {
  let r, m;
  let divsgn = 1;
  if (y === 0) {
    throw new Error('division by zero in rounddiv');
  }
  else {
    if (y < 0) {
      divsgn = -divsgn;
      y = -y;
    }
  }
  if (x < 0) {
    divsgn = -divsgn;
    x = -x;
  }
  r = Math.trunc(x / y);
  m = x % y;
  if (2 * m >= y) {
    r++;
  }
  return divsgn * r;
}
